package es.curso.utilidades;

public class Calculadora {

	public double sumar(double a, double b) {

		return a + b;
	}

	public double restar(double a, double b) {

		return a - b;
	}

	public double multiplicar(int a, int b) {
		// TODO Auto-generated method stub
		return a*b;
	}

}
